select mhs_nama, tb_mahasiswa_nilai.nilai, tb_matakuliah.mk_kode from tb_mahasiswa_nilai join tb_mahasiswa join tb_matakuliah where tb_mahasiswa.mhs_id=tb_mahasiswa_nilai.mhs_id and tb_matakuliah.mk_kode='MK303' and tb_matakuliah.mk_id=tb_mahasiswa_nilai.mk_id order by tb_mahasiswa_nilai.nilai desc LIMIT 1;